definition.script:
library "jenkinsci-unstashParam-library"
pipeline {
    agent {label 'ansible'}

    stages {
        stage('Update Blueprint') {
            when {
                expression { params.ACTIONS == 'CE-CUTOVER' || params.ACTIONS == 'ALL-STEPS' }
            }
            steps {
                sh "mkdir ${BUILD_NUMBER}"




                script {
                def myarr = "${SERVERNAMES}".split('\n')

                def file_in_workspace = unstashParam "variables.yaml"
                sh "mv '${file_in_workspace}' ${BUILD_NUMBER}/variables.yaml"

                echo 'Hello World'

                sh """



                set +x

                sudo cp /var/lib/docker/volumes/jenkins_home/_data/migrationpipeline/ce_login.py ${BUILD_NUMBER}/

                """
                dir("${env.BUILD_NUMBER}") {
                sh "pwd"
                myarr.each{ server ->

                    sh """
                    set +x
                    echo ${server}

	                sudo python3 ce_login.py ${server} ${CEAPIToken} ${CEProjectName}

                    #done


                    """ 
                }
                }

            }
            }
        }
        stage('Validate platforms')
        {
            when {
                expression { params.ACTIONS == 'CE-CUTOVER' || params.ACTIONS == 'ALL-STEPS' }
            }
            steps {

              script {
            def platform_check = ''

            sh """
            sudo cp /var/lib/docker/volumes/jenkins_home/_data/migrationpipeline/validate_platform.py ${BUILD_NUMBER}/
            """
            dir("${env.BUILD_NUMBER}") {

                platform_check=sh (returnStdout: true, script: "python3 validate_platform.py")


            if ( platform_check != '' ){
                println "The list has a mix of Windows and Linux Servers. Please submit only one type of servers per job."
                currentBuild.result = 'ABORTED'
                error('Stopping earlyâ€¦')
            }
            }
              }

            }

        }        
        stage('Launch Instances'){
            when {
                expression { params.ACTIONS == 'CE-CUTOVER' || params.ACTIONS == 'ALL-STEPS' }
            }
            steps {
            dir("${env.BUILD_NUMBER}") {
                sh """
                set +x
                sudo cp /var/lib/docker/volumes/jenkins_home/_data/migrationpipeline/ce_launch.py .

                sudo python3 ce_launch.py
            """
            }    

            }
        }
        stage('CloudEndure Job Running'){
            when {
                expression { params.ACTIONS == 'CE-CUTOVER' || params.ACTIONS == 'ALL-STEPS' }
            }
            steps {
            dir("${env.BUILD_NUMBER}") {
            sh """
            sudo cp /var/lib/docker/volumes/jenkins_home/_data/migrationpipeline/ce_checkjob.py .

            sudo python3 ce_checkjob.py
            """
            }
            }
        }
        stage('Check EC2 Status'){
            when {
                expression { params.ACTIONS == 'CHECK-EC2' || params.ACTIONS == 'CE-CUTOVER' || params.ACTIONS == 'ALL-STEPS' }
            }
            steps {

                sh """
                set +x
                if [ ! -d ${BUILD_NUMBER} ]
                then
                    mkdir ${BUILD_NUMBER}
                fi
                sudo cp /opt/migrations/scripts/get_instances.sh ${BUILD_NUMBER}/
                sudo chmod 755 ${BUILD_NUMBER}/get_instances.sh
                """
                script {
                def myarr2 = "${SERVERNAMES}".split('\n')
                     sh """
                        set +x
                        if [ -f ${BUILD_NUMBER}/${filename} ]; then
                            rm -rf ${BUILD_NUMBER}/${filename}
                        fi

                    """
                    myarr2.each{ server ->

                    sh """
                    set +x

                    echo ${server}
                    echo ${server} >> ${BUILD_NUMBER}/${filename}

                    """
                    }

                }
                dir("${env.BUILD_NUMBER}") {
                    sh "cat ${filename}"
                    withCredentials([[$class: 'AmazonWebServicesCredentialsBinding', accessKeyVariable: 'AWS_ACCESS_KEY_ID', credentialsId: 'Staging_CE', secretKeyVariable: 'AWS_SECRET_ACCESS_KEY']]) {
                        sh """
                        set +x
                        export AWS_ACCESS_KEY_ID=$AWS_ACCESS_KEY_ID
                        export AWS_SECRET_ACCESS_KEY=$AWS_SECRET_ACCESS_KEY
                        export AWS_DEFAULT_REGION='ap-southeast-2'
                        ./get_instances.sh ${filename} ${platform} ${TARGET_AWS_REGION}
                        """
                    //}

                        script {
                            def myarr = "${SERVERNAMES}".split('\n')
                            countofserver = myarr.size()
                            int countofrunning = 0
                            println countofserver
                            println countofrunning
                                while (countofrunning != countofserver){
                                    countofrunning = 0
                                    myarr.each{ server ->
                                    def state = ''
                                    def instance_status = ''
                                    def system_status = ''
                                    def instance = ''
                                    def file =  readFile("${env.WORKSPACE}/${env.BUILD_NUMBER}/${filename}_instances")
                                    def lines = file.readLines()
                                        lines.each { String line ->

                                        if (line.contains("${server}")){
                                            instance = line.split(":")[1]
                                            state = sh (returnStdout: true, script: "aws ec2 describe-instances --instance-id ${instance} --region ${TARGET_AWS_REGION} | jq -r .Reservations[].Instances[].State.Name")
                                            instance_status = sh (returnStdout: true, script: "aws ec2 describe-instance-status --instance-id ${instance} --region ${TARGET_AWS_REGION} | jq -r .InstanceStatuses[].InstanceStatus.Details[].Status")
                                            system_status = sh (returnStdout: true, script: "aws ec2 describe-instance-status --instance-id ${instance} --region ${TARGET_AWS_REGION} | jq -r .InstanceStatuses[].SystemStatus.Details[].Status")

                                            println "State of "+server+"is:"+state
                                            println "instance status of "+server+"is:"+instance_status
                                            println "system status of "+server+"is:"+system_status
                                            if (state.contains("running") && instance_status.contains("passed") && system_status.contains("passed")){
                                                println server+"is passed.."
                                                countofrunning = countofrunning + 1
                                            }
                                            println "waiting..."
                                        }


                                        }

                                    }
                                    sh "echo  'Total ${countofrunning} started out of ${countofserver}...'"
                                }
                        }
                }
                }
            }



        }
        stage('Run Ansible-Prewig'){
            when {
                expression { params.ACTIONS == 'PRE-WIG' || params.ACTIONS == 'ALL-STEPS' }
            }
            steps{


                withCredentials([[$class: 'AmazonWebServicesCredentialsBinding', accessKeyVariable: 'AWS_ACCESS_KEY_ID', credentialsId: 'Staging_CE', secretKeyVariable: 'AWS_SECRET_ACCESS_KEY'], string(credentialsId: 'ansible_pswd', variable: 'ansible_pswd')]) {
                sh """
                set +x
                if [ ! -d ${BUILD_NUMBER} ]
                then
                    mkdir ${BUILD_NUMBER}
                fi
                sudo cp /opt/migrations/scripts/stop_instances.sh ${BUILD_NUMBER}/
                sudo cp /opt/migrations/scripts/start_instances.sh ${BUILD_NUMBER}/
                sudo cp /opt/migrations/scripts/get_instances.sh ${BUILD_NUMBER}/
                """
                script {
                if ( params.ACTIONS == 'ALL-STEPS' )
                {

                    sleep(420)

                }
                def myarr2 = "${SERVERNAMES}".split('\n')
                    sh """
                    if [ -f ${BUILD_NUMBER}/${filename} ]; then
                        rm -rf ${BUILD_NUMBER}/${filename}
                    fi
                    """


                    myarr2.each{ server ->

                    sh """

                    echo ${server}
                    echo ${server} >> ${BUILD_NUMBER}/${filename}

                    """
                    }


                }    

                script{
                    dir("${env.BUILD_NUMBER}") {


                        sh """
                          set +x
                          export AWS_ACCESS_KEY_ID=$AWS_ACCESS_KEY_ID
                          export AWS_SECRET_ACCESS_KEY=$AWS_SECRET_ACCESS_KEY
                          export AWS_DEFAULT_REGION='ap-southeast-2'
                          ./get_instances.sh ${filename} ${platform} ${TARGET_AWS_REGION}
                          if [[ "${platform}" == "linux"  && "${tasks}" == "ALL" ]]
                          then
                           ansible -i ${filename}_ansible ${filename} -m ping --extra-vars "ansible_password=${ansible_pswd}"
                           ansible-playbook -i ${filename}_ansible -l ${filename} /opt/migrations/automation-scripts/playbook-linux-rhel-prewig.yaml --extra-vars "ansible_password=${ansible_pswd}"
                          elif [[ "${platform}" == "linux" && "${tasks}" == "runvalidator" ]]
                          then
                           ansible-playbook -i ${filename}_ansible -l ${filename} /opt/migrations/automation-scripts/playbook-linux-rhel-prewig.yaml --extra-vars "ansible_password=${ansible_pswd}" --tags ${tasks}
                          elif [[ "${platform}" == "windows" && "${tasks}" == "ALL" ]]
                          then
                           ansible -i ${filename}_ansible ${filename} -m win_ping --extra-vars "ansible_password=${ansible_pswd}"
                           ansible-playbook -i ${filename}_ansible -l ${filename} /opt/migrations/automation-scripts/playbook-windows-prewig.yaml --extra-vars "ansible_password=${ansible_pswd}"
                          elif [[ "${platform}" == "windows" && "${tasks}" == "winrunvalidator" ]]
                          then
                           ansible-playbook -i ${filename}_ansible -l ${filename} /opt/migrations/automation-scripts/playbook-windows-prewig.yaml --extra-vars "ansible_password=${ansible_pswd}" --tags ${tasks}
                          fi
                        """

                    }
                }
             }
            }
        }
        stage("Create Pre-ingestion AMI"){
            when {
                expression { params.ACTIONS == 'CREATE-AMI' || params.ACTIONS == 'ALL-STEPS' }
            }
            steps{
                withCredentials([[$class: 'AmazonWebServicesCredentialsBinding', accessKeyVariable: 'AWS_ACCESS_KEY_ID', credentialsId: 'Staging_CE', secretKeyVariable: 'AWS_SECRET_ACCESS_KEY']]) {
                sh """
                set +x
                if [ ! -d ${BUILD_NUMBER} ]
                then
                    mkdir ${BUILD_NUMBER}
                fi
                sudo cp /opt/migrations/scripts/stop_instances.sh ${BUILD_NUMBER}/
                sudo cp /opt/migrations/scripts/start_instances.sh ${BUILD_NUMBER}/
                sudo cp /opt/migrations/scripts/get_instances.sh ${BUILD_NUMBER}/
                sudo cp -r /opt/migrations/scripts/amsrfc ${BUILD_NUMBER}/
                sudo chown -R cloudxmigration:root ${BUILD_NUMBER}/amsrfc/
                """
                script {
                    if ( params.AMP_EMAIL_ID == '' || params.AMP_PASSWORD.getPlainText().isEmpty() ) { 
                    currentBuild.result = 'ABORTED'
                    error('AMP_EMAIL_ID or AMP_PASSWORD is not provided..')
                    }
                    if ( params.AMP_PASSWORD.getPlainText() != params.AMP_PASSWORD2.getPlainText()){
                    currentBuild.result = 'ABORTED'
                    error('Password and Re-entered Password do not match..')

                    }

                }
                script {
                def myarr2 = "${SERVERNAMES}".split('\n')
                    sh """
                        set +x
                        if [ -f ${BUILD_NUMBER}/${filename} ]; then
                            rm -rf ${BUILD_NUMBER}/${filename}
                        fi

                    """
                    myarr2.each{ server ->

                    sh """

                    echo ${server}
                    echo ${server} >> ${BUILD_NUMBER}/${filename}

                    """
                    }

                }        
                script{



                    dir("${env.BUILD_NUMBER}") {
                        sh """
                        set +x
                        export AWS_ACCESS_KEY_ID=$AWS_ACCESS_KEY_ID
                        export AWS_SECRET_ACCESS_KEY=$AWS_SECRET_ACCESS_KEY
                        export AWS_DEFAULT_REGION='ap-southeast-2'
                        ./get_instances.sh ${filename} ${platform} ${TARGET_AWS_REGION}
                        echo ${filename} > temp
                        echo "1" >> temp
                        ./stop_instances.sh < temp
                        rm -f temp

                        """   
                    }
                    dir("${env.BUILD_NUMBER}/amsrfc/RFC_CREATE_SCRIPTS") {
                        sh """
                        set +x
                        sudo su
                        export AZURE_TENANT_ID=${AZURE_TENANT_ID}
                        export AZURE_APP_ID_URI=${AZURE_APP_ID_URI}
                        export AZURE_DEFAULT_USERNAME=${AMP_EMAIL_ID}
                        export AZURE_DEFAULT_PASSWORD=${AMP_PASSWORD}
                        export AZURE_DEFAULT_ROLE_ARN=${AZURE_DEFAULT_ROLE_ARN}
                        export AZURE_DEFAULT_DURATION_HOURS=${AZURE_DEFAULT_DURATION_HOURS}
                        export https_proxy=http://c2proxy.ampaws.com.au:8080/
                        sudo -E aws-azure-login --no-sandbox --no-prompt
                        sudo ./create_AMIRFCv2.sh 'pre-ingestion' ${filename}
                        sudo ./check_AMSRFCv3.sh 'rfc_list.txt'

                        """

                    }
                }    
            }

            }


        }
        stage('Start Instances'){
            when {
                expression { params.ACTIONS == 'CREATE-AMI' || params.ACTIONS == 'ALL-STEPS' }
            }
            steps{
                withCredentials([[$class: 'AmazonWebServicesCredentialsBinding', accessKeyVariable: 'AWS_ACCESS_KEY_ID', credentialsId: 'Staging_CE', secretKeyVariable: 'AWS_SECRET_ACCESS_KEY']]) {
                script {
                sh """
                set +x
                if [ ! -d ${BUILD_NUMBER} ]
                then
                    mkdir ${BUILD_NUMBER}
                fi
                sudo cp /opt/migrations/scripts/stop_instances.sh ${BUILD_NUMBER}/
                sudo cp /opt/migrations/scripts/start_instances.sh ${BUILD_NUMBER}/
                sudo cp /opt/migrations/scripts/get_instances.sh ${BUILD_NUMBER}/

                """
                dir("${env.BUILD_NUMBER}") {
                        sh """
                        set +x
                        export AWS_ACCESS_KEY_ID=$AWS_ACCESS_KEY_ID
                        export AWS_SECRET_ACCESS_KEY=$AWS_SECRET_ACCESS_KEY
                        export AWS_DEFAULT_REGION='ap-southeast-2'
                        echo ${filename} > temp
                        echo "1" >> temp
                        ./start_instances.sh < temp
                        rm -f temp

                        """   
                    }
                }

                }


            }

        }
        stage('Raise WIG RFC'){
            when {
                expression { params.ACTIONS == 'WIGRFC' }
            }
            steps{
                withCredentials([[$class: 'AmazonWebServicesCredentialsBinding', accessKeyVariable: 'AWS_ACCESS_KEY_ID', credentialsId: 'Staging_CE', secretKeyVariable: 'AWS_SECRET_ACCESS_KEY']]) {
                    script {
                    if ( params.AMP_EMAIL_ID == '' || params.AMP_PASSWORD.getPlainText().isEmpty() ) { 
                    currentBuild.result = 'ABORTED'
                    error('AMP_EMAIL_ID or AMP_PASSWORD is not provided..')
                    }
                    if ( params.AMP_PASSWORD.getPlainText() != params.AMP_PASSWORD2.getPlainText()){
                    currentBuild.result = 'ABORTED'
                    error('Password and Re-entered Password do not match..')

                    }

                }
                    script {
                        sh """
                        set +x
                        if [ ! -d ${BUILD_NUMBER} ]
                        then
                            mkdir ${BUILD_NUMBER}
                        fi
                        yes | sudo cp /opt/migrations/scripts/get_instances.sh ${BUILD_NUMBER}/
                        yes | sudo cp -r /opt/migrations/scripts/amsrfc ${BUILD_NUMBER}/
                        sudo chown -R cloudxmigration:root ${BUILD_NUMBER}/amsrfc/
                        """
                        def myarr2 = "${SERVERNAMES}".split('\n')
                        sh """
                        set -x
                        if [ -f ${BUILD_NUMBER}/${filename} ]; then
                            rm -rf ${BUILD_NUMBER}/${filename}
                        fi

                        """
                        myarr2.each{ server ->

                        sh """

                        echo ${server}
                        echo ${server} >> ${BUILD_NUMBER}/${filename}

                        """
                        }

                    }  
                    script {

                        dir("${env.BUILD_NUMBER}") {
                            sh """
                            set +x
                            export AWS_ACCESS_KEY_ID=$AWS_ACCESS_KEY_ID
                            export AWS_SECRET_ACCESS_KEY=$AWS_SECRET_ACCESS_KEY
                            export AWS_DEFAULT_REGION='ap-southeast-2'
                            ./get_instances.sh ${filename} ${platform} ${TARGET_AWS_REGION}
                            """   
                        }
                        dir("${env.BUILD_NUMBER}/amsrfc/RFC_CREATE_SCRIPTS") {
                            sh """
                            set +x
                            sudo su
                            export AZURE_TENANT_ID=${AZURE_TENANT_ID}
                            export AZURE_APP_ID_URI=${AZURE_APP_ID_URI}
                            export AZURE_DEFAULT_USERNAME=${AMP_EMAIL_ID}
                            export AZURE_DEFAULT_PASSWORD=${AMP_PASSWORD}
                            export AZURE_DEFAULT_ROLE_ARN=${AZURE_DEFAULT_ROLE_ARN}
                            export AZURE_DEFAULT_DURATION_HOURS=${AZURE_DEFAULT_DURATION_HOURS}
                            export https_proxy=http://c2proxy.ampaws.com.au:8080/
                            sudo -E aws-azure-login --no-sandbox --no-prompt
                            sudo ./create_WIGRFCv2.sh ${filename} ${wig_vpc} ${wig_subnet}
                            """
                        }
                    }
                }

            }

        }


    }
}
